import { InfoCircleFill, Globe, BadgeCcFill } from "react-bootstrap-icons";
import "./CourseDescription.css";

function CourseDescription({ courseData }) {
  return (
    <div className="courseHeader">
      <div class = "fw-bold" style = {{color:"blueviolet"}}>
        <span>  Development </span> <span style = {{verticalAlign: "middle"}} class="material-icons">navigate_next</span>
         <span >Programming Languages </span> <span style = {{verticalAlign: "middle"}}  class="material-icons">navigate_next</span>
           <span>Python</span>
      </div>
      <h2 style={{ width: "600px"}}>{ localStorage.CourseTitle}</h2>
      <p>{courseData.headline}</p>
      <div className="otherData">
        <div>{courseData.rating}</div>
        <div>{courseData.people}</div>
      </div>
      <div>
        Created by <span>{courseData.instructors}</span>
      </div>
      <div className="otherData">
        <div>
          <InfoCircleFill /> Last updated {courseData.lastUpdateDate}
        </div>
        <div>
          <Globe /> {courseData.lang}
        </div>
        <div>
          <BadgeCcFill /> {courseData.captionLang}
        </div>
      </div>
    </div>
  );
}

export default CourseDescription;
